mapper.insert:
insert into xm06_t_conference (id, conference_name, conference_info, 
  conference_date, hour_long, address, 
  creator_id, create_date, group_id
  )
values (#{id,jdbcType=INTEGER}, #{conferenceName,jdbcType=VARCHAR}, #{conferenceInfo,jdbcType=VARCHAR}, 
  #{conferenceDate,jdbcType=TIMESTAMP}, #{hourLong,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, 
  #{creatorId,jdbcType=INTEGER}, #{createDate,jdbcType=TIMESTAMP}, #{groupId,jdbcType=INTEGER}
  )

mapper.select:
SELECT * FROM xm06_t_conference
WHERE group_id = #{groupId}
order by create_date desc

SELECT c.* FROM xm06_t_conference c
WHERE c.creator_id = #{creatorId}
order by create_date desc

select * from xm06_t_conference
where
  conference_name = #{conferenceName, jdbcType=VARCHAR}
  and
  creator_id = #{creatorId, jdbcType=INTEGER}
