mapper.insert:
insert into t_project (id, project_name, project_desc, 
  creator_id, group_id, completion_degree, 
  create_date, update_date, is_valid
  )
values (#{id,jdbcType=INTEGER}, #{projectName,jdbcType=VARCHAR}, #{projectDesc,jdbcType=VARCHAR}, 
  #{creatorId,jdbcType=INTEGER}, #{groupId,jdbcType=INTEGER}, #{completionDegree,jdbcType=DOUBLE}, 
  #{createDate,jdbcType=TIMESTAMP}, #{updateDate,jdbcType=TIMESTAMP}, #{isValid,jdbcType=BIT}
  )

mapper.select:
select * from t_project
where
project_name = #{projectName, jdbcType=VARCHAR}
and
group_id = #{groupId, jdbcType=INTEGER}

select * from t_project
where
    group_id = #{groupId, jdbcType=INTEGER}

select * from t_project
where
    creator_id = #{creatorId, jdbcType=INTEGER}
